CREATE TABLE "CIDADE" ( `HANDLE` INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT UNIQUE, `CEP` TEXT NOT NULL, `NOME` TEXT NOT NULL, `ESTADO` TEXT NOT NULL, `DATACRIACAO` TEXT NOT NULL, `DATAALTERACAO` TEXT )
CREATE TABLE "CLIENTE" ( `HANDLE` INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT UNIQUE, `CIDADE` INTEGER NOT NULL, `NOME` TEXT NOT NULL, `SEXO` TEXT NOT NULL, `SALDO` INTEGER, `NASCIMENTO` TEXT NOT NULL, `ATIVO` INTEGER NOT NULL, `DATACRIACA` TEXT NOT NULL, `DATAALTERACAO` TEXT, FOREIGN KEY(`CIDADE`) REFERENCES `CIDADE`(`HANDLE`) )
CREATE TABLE "PRODUTO" ( `HANDLE` INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT UNIQUE, `NOME` TEXT NOT NULL, `DESCRICAO` TEXT NOT NULL, `PRECO` REAL NOT NULL, `ESTOQUE` INTEGER NOT NULL, `DATACRIACAO` TEXT NOT NULL, `DATAALTERACAO` TEXT )
CREATE TABLE "USUARIO" ( `HANDLE` INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT UNIQUE, `LOGIN` TEXT NOT NULL, `SENHA` TEXT NOT NULL, `APAGA` INTEGER NOT NULL, `ESCREVE` INTEGER NOT NULL, `VENDAS` INTEGER, `NOME` TEXT NOT NULL, `DATACRIACAO` TEXT NOT NULL, `DATAALTERACAO` TEXT )
CREATE TABLE "VENDA" ( `HANDLE` INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT UNIQUE, `CLIENTE` INTEGER NOT NULL, `VENDEDOR` INTEGER NOT NULL, `PRODUTO` INTEGER NOT NULL, `DATA` TEXT NOT NULL, `DATA_PGT` TEXT NOT NULL, `VALOR` REAL NOT NULL, `AJUSTE` REAL, `TOTAL` REAL NOT NULL, `DATACRIACAO` TEXT NOT NULL, `DATAALTERACAO` TEXT, FOREIGN KEY(`VENDEDOR`) REFERENCES `USUARIO`(`HANDLE`), FOREIGN KEY(`PRODUTO`) REFERENCES `VENDA_PRODUTO`(`HANDLE`), FOREIGN KEY(`CLIENTE`) REFERENCES `CLIENTE`(`HANDLE`) )
CREATE TABLE "VENDA_PRODUTO" ( `HANDLE` INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT UNIQUE, `PRODUTO` INTEGER NOT NULL, `QUANTIDADE` INTEGER NOT NULL, FOREIGN KEY(`PRODUTO`) REFERENCES `PRODUTO`(`HANDLE`) )
